import React, { Component } from 'react';
// import PictureSlide from './components/Carousel'
// import Parallax from '../Parallax/Parallax';
import { Jumbotron, Button } from 'reactstrap';

const images = [
  require('../../assets/profilePic.jpg')
];

const styles = {
  h1: {
    fontFamily: 'Open Sans',
    fontSize: '25px',
    position: 'relative',
    maxWidth: '50%',
    paddingLeft: '20rem',
    paddingTop: '4rem'
  },
  font: {
    fontSize: '20px',
    fontFamily: 'Open Sans',
    fontWeight: 'normal',
    position: 'relative',
    maxWidth: '50%',
    paddingLeft: '5rem'
  },
  background: {
    backgroundColor: '#ffffff',
    paddingBottom: '10rem'
  },
  image: {
    position: 'fixed',
    maxWidth: '200px',
    maxHeight: '400px',
    // marginRight: 'auto',
    display: 'block',
    left: '55rem',
    top: '3.5rem'
  }
};

class Home extends Component {
  render () {
    return (
      <>
        <Jumbotron style={styles.background}>
          <img src={images[0]} style={styles.image} alt='' />
          <h1 style={styles.h1}>Experience</h1>
          <p style={styles.font}>Web developer with experience in development of full stack applications with experience in <strong>HTML5, CSS3, React, JavaScript, Node, Express, MySQL, and MongoDB.</strong> Certified from the University of California Division of continued education.</p>
          <h1 style={styles.h1}>My drive</h1>
          <p style={styles.font}>Since I was a kid I always loved learning new things. From football to dirt bikes, to cars and fishing just to name a few. Over the years I always found myself doing one thing that mattered the most. Helping people. Whether it would be teaching them , empathizing, with them, or just help solve their problems. There was always a joy in it! In turn I learned much from those very people. One of them being my cousin who introduced me to software. He showed me a website he had built that was helping hundreds of people all over the world. I was already intrigued from the beginning, but to see the benefit it was providing people and the community it formed, I was game. So I took the keyboard and started my journey to take my problem solving skills worldwide. Now I am seeking to leverage my cogent, technical, and professional communicative skills to learn and grow in the new role as a web developer.</p>
        </Jumbotron>
      </>
    );
  }
}

export default Home
;
