import React, { Component } from 'react';
import { Form, FormGroup, Input, Label, Button } from 'reactstrap';

const styles = {
  color: {
    backgroundColor: '#1D56DB'
  },
  size: {
    padding: '2rem'
  },
  font: {
    fontSize: '80px',
    marginTop: '1rem'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    position: 'relative',
    top: '10rem',
    textAlign: 'center',
    maxWidth: '15rem',
    margin: 'auto',
    padding: '1rem'
  },
  btn: {
    margin: 'auto',
    display: 'flex',
    position: 'relative',
    top: '10rem'

  }
};

class Contact extends React.Component {
  render () {
    return (
      <Form>
        <FormGroup>
          <Label for='name'>Name</Label>
          <Input
            type='text'
            name='name'
            onChange={this.handleChange}
          />
        </FormGroup>
        <FormGroup>
          <Label for='name'>Email</Label>
          <Input
            type='email'
            name='email'
            onChange={this.handleChange}
          />
        </FormGroup>
        <FormGroup>
          <Label for='name'>Message</Label>
          <Input
            type='textarea'
            name='message'
            onChange={this.handleChange}
          />
        </FormGroup>
      </Form>
    );
  }
}

export default Contact;
