import React, { Component } from 'react';
import axios from 'axios';

const styles = {
    form: {
      display: 'flex',
      flexDirection: 'column',
      paddingTop: '10rem',
      textAlign: 'center',
      maxWidth: '15rem',
      margin: 'auto'
    },
    input: {
      paddingTop: '10px'
    }
}

class Contact extends Component {

    state = {
        name: '',
        message: '',
        email: '',
        sent: false,
        buttonText: 'Send Message'
    }
    

    render() {
        return(
          <form style={styles.form} className="contact-form" onSubmit={ (e) => this.formSubmit(e)}>
        
          <label class="message-name" htmlFor="message-name">Your Name</label>
          <input styles={styles.input} onChange={e => this.setState({ name: e.target.value})} name="name" class="message-name" type="text" placeholder="Your Name" value={this.state.name}/>
        
          <label class="message-email" htmlFor="message-email">Your Email</label>
          <input styles={styles.input} onChange={(e) => this.setState({ email: e.target.value})} name="email" class="message-email" type="email" placeholder="your@email.com" required value={this.state.email} />
        
          <label class="message" htmlFor="message-input">Your Message</label>
          <textarea onChange={e => this.setState({ message: e.target.value})} name="message" class="message-input" type="text" placeholder="Please write your message here" value={this.state.message} required/>
          <div className="button--container">
              <button type="submit" className="button button-primary">{ this.state.buttonText }</button>
          </div>
        </form>
        );
    }
}

export default Contact;
